import React, { useState, useEffect } from "react";
import { Demo } from "./AntTree";
import { GeneralDisplayPage } from "./GeneralManage";
import { Link } from "react-router-dom";
import { loadCourses, deleteCourse } from "../actions/courseActions";

function CoverPage() {
  const [KeySelected, setKeySelected] = useState({
    key: "1",
    isEdit: true,
    type: "Branch"
  });

  //useEffect(() => {}, [KeySelected]);

  const onClick = value => {
    setKeySelected({ key: "0", isEdit: false, type: value.target.value });
  };

  const onChange = selectedKeys => {
    if (selectedKeys[0] !== undefined) {
      let split = selectedKeys[0].split(",");
      setKeySelected({
        key: split[0],
        isEdit: true,
        type: split[1] === "E" ? "Employee" : "Branch"
      });
    }
  };

  return (
    <>
      <h2>Hunting 2.0!</h2>
      add Tree here
      <div className="row">
        <div className="col-md-2"></div>
        <div className="col-md-2">
          <Demo mySelect={onChange} myClick={onClick} />
        </div>
        <div className="col-md-2"></div>
        <div className="col-md-2">
          <GeneralDisplayPage
            GenKey={KeySelected.key}
            isEdit={KeySelected.isEdit}
            type={KeySelected.type}
          />
        </div>
        <div className="col-md-6"></div>
      </div>
    </>
  );
}

export default CoverPage;
