import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import "antd/dist/antd.css";
import { Form, Input, InputNumber, Button } from "antd";

import {
  getSpecificEmployeeByKey,
  getSpecificBranchByKey,
  PostData
} from "./MockData";

const layout = {
  labelCol: {
    span: 8
  },
  wrapperCol: {
    span: 16
  }
};
const validateMessages = {
  required: "This field is required!",
  types: {
    email: "Not a validate email!",
    number: "Not a validate number!"
  },
  number: {
    range: "Must be between ${min} and ${max}"
  }
};
const defaultBranch = [
  {
    key: "0",
    data: [
      {
        name: "BranchCode",
        value: ""
      },
      {
        name: "BranchName",
        value: ""
      },
      {
        name: "BranchCategory",
        value: ""
      },
      {
        name: "ParentBranch",
        value: ""
      }
    ]
  }
];
const defaultEmployee = [
  {
    key: "0",
    data: [
      {
        name: "EmployeeNumber",
        value: ""
      },
      {
        name: "EmployeeName",
        value: ""
      },
      {
        name: "EmployeeSurname",
        value: ""
      },
      {
        name: "EmployeeLastLoggedIn",
        value: ""
      },
      {
        name: "EmployeeID",
        value: ""
      },
      {
        name: "EmployeeBranch",
        value: 0
      }
    ]
  }
];
export function GeneralDisplayPage(props) {
  const [Data, setData] = useState(defaultBranch);
  const [form] = Form.useForm();

  const onFinish = values => {
    console.log(values);
    PostData(props.type, props.isEdit, values);
    //post it
  };
  useEffect(() => {
    if (props.GenKey !== undefined) {
      switch (props.type) {
        case "Locale":
        case "Branch":
          setData(
            props.isEdit ? getSpecificBranchByKey(props.GenKey) : defaultBranch
          );
          break;
        case "Employee":
          setData(
            props.isEdit
              ? getSpecificEmployeeByKey(props.GenKey)
              : defaultEmployee
          );
          break;
        default:
          break;
      }
    }
    form.setFieldsValue({});
  }, [props.GenKey, props.isEdit, props.type]);
  return (
    <div>
      {Data.map(memes => {
        return (
          <>
            <h2>
              {props.isEdit
                ? `Editing for ${props.GenKey}`
                : `Adding new ${props.type}`}
            </h2>
            <Form
              form={form}
              {...layout}
              name="nest-messages"
              onFinish={onFinish}
              validateMessages={validateMessages}
            >
              {memes.data.map(field => {
                return (
                  <Form.Item label={field.name} name={field.name}>
                    <Input label={field.name} value={{ value: "test" }} />
                  </Form.Item>
                );
              })}
              <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
                <Button type="primary" htmlType="submit">
                  Submit
                </Button>
              </Form.Item>
            </Form>
          </>
        );
      })}
    </div>
  );
}

GeneralDisplayPage.propTypes = {
  GenKey: PropTypes.string.isRequired,
  isEdit: PropTypes.bool.isRequired,
  type: PropTypes.string.isRequired
};

export default GeneralDisplayPage;
