import React from "react";
import PropTypes from "prop-types";
import "antd/dist/antd.css";
import { Popover, Button } from "antd";

export function popup(props) {
  const [rightClickNodeTreeItem, setrightClickNodeTreeItem] = useState({
    pageX: "",
    pageY: "",
    id: "",
    categoryName: ""
  });
  const onRightClick = e => {
    setrightClickNodeTreeItem({
      pageX: e.event.pageX,
      pageY: e.event.pageY,
      id: e.node.props["data-key"],
      categoryName: e.node.props["data-title"]
    });
  };
  const { pageX, pageY, id, categoryName } = {
    ...rightClickNodeTreeItem
  };
  const tmpStyle = {
    position: "absolute",
    left: `${pageX - 220}px`,
    top: `${pageY - 102}px`
  };
  const menu = (
    <div style={tmpStyle} className="self-right-menu">
      <a> new lower sector </a>
      <a> modify </a>
      <a> delete the directory </a>
    </div>
  );
}
popup.propTypes = {
  onClick: PropTypes.func.isRequired,
  Key: PropTypes.string.isRequired,
  isAddable: PropTypes.bool.isRequired
};
