import React, { useEffect, useState, Fragment } from "react";
import { Tree, Button, Popover, Form } from "antd";
import PropTypes from "prop-types";
import "antd/dist/antd.css";
import { GetTreeData, GetTreeChildren } from "./MockData";
import axios from "axios";
//import { onRightClick } from "./popup";

function updateTreeData(list, key, children) {
  return list.map(node => {
    if (node.key === key) {
      return { ...node, children };
    }
    if (node.children) {
      return {
        ...node,
        children: updateTreeData(node.children, key, children)
      };
    }

    return node;
  });
}
function GetChildNodes(ParentKey) {
  let children = GetTreeChildren(ParentKey);
  return children;
}

export function Demo(props) {
  const [treeData, setTreeData] = useState(GetTreeData);
  const url = `https://localhost:5001/api/trees`;

  let DataTree = [];
  useEffect(() => {
    async function fetchData() {
      debugger;
      let obj = [];

      const response = await fetch(url);
      // axios
      //   .get(url)
      //   .then(response => {
      //     console.log(response);
      //     response.data.map(Main => {
      //       obj.push({
      //         title: Main.data[0].value,
      //         key: Main.data[1].value,
      //         isLeaf: Main.data[2].value
      //       });
      //     });
      //   })
      //   .catch(error => {
      //     console.log(error);
      // //   });
      // response.data.map(Main => {
      //   obj.push({
      //     title: Main.data[0].value,
      //     key: Main.data[1].value,
      //     isLeaf: Main.data[2].value
      //   });
      // });
      // return obj;
    }
    //setTreeData(fetchData());
    //DataTree = fetchData();
  });
  const layout = {
    labelCol: {
      span: 8
    },
    wrapperCol: {
      span: 16
    }
  };
  const tailLayout = {
    wrapperCol: {
      offset: 8,
      span: 16
    }
  };
  function onLoadData({ key, children }) {
    return new Promise(resolve => {
      if (children) {
        resolve();
        return;
      }

      setTreeData(origin => updateTreeData(origin, key, GetChildNodes(key)));
      resolve();
    });
  }

  // const onSelect = (selectedKeys, otherValue) => {
  //   debugger;
  //   console.log(selectedKeys);
  //   console.log(otherValue);
  // };

  return (
    <Fragment>
      {/* <Popover
        content={<a onClick={hide}>Close</a>}
        title="Title"
        trigger="click"
        visible={Visible}
        onVisibleChange={handleVisibleChange}
      ></Popover> */}
      <Tree
        {...layout}
        style={{ height: "auto", minHeight: "570px" }}
        loadData={onLoadData}
        onSelect={props.mySelect}
        treeData={treeData}
      />
      <Form.Item>
        <Button type="button" onClick={() => props.myClick("Branch")}>
          Add new Branch
        </Button>
        <Button
          type="button"
          onClick={() => props.myClick("Employee")}
          value="Employee"
        >
          Add new Employee
        </Button>
      </Form.Item>
    </Fragment>
  );
}

//TODO: move the keys away from a delimited string
//      add popup instead of buttons to add
Demo.propTypes = {
  mySelect: PropTypes.func.isRequired,
  myClick: PropTypes.func.isRequired
};

export default Demo;
