import React, { useState, useEffect } from "react";
import { Demo } from "./AntTree";
import {
  Layout,
  Button,
  Radio,
  Tag,
  Row,
  Col,
  Form,
  Select,
  Icon,
  Spin,
  LocaleProvider,
  message
} from "antd";
import { GeneralDisplayPage } from "./GeneralManage";
import { Link } from "react-router-dom";
import { loadCourses, deleteCourse } from "../actions/courseActions";

function CoverPage() {
  const [KeySelected, setKeySelected] = useState({
    key: "1",
    isEdit: true,
    type: "Branch"
  });

  //useEffect(() => {}, [KeySelected]);

  const onClick = value => {
    setKeySelected({ key: "0", isEdit: false, type: value });
  };

  const onChange = (selectedKeys, type) => {
    console.log("Trigger Select" + selectedKeys + type.node.isLeaf);
    if (selectedKeys[0] !== undefined) {
      setKeySelected({
        key: selectedKeys[0],
        isEdit: true,
        type: type.node.isLeaf ? "Employee" : "Branch"
      });
    }
  };

  return (
    <Layout>
      <Spin spinning={false}>
        <h2>Hunting 2.0!</h2>
        <Row gutter={90}>
          <Col className="row">
            <Col span={4}></Col>
            <Col
              span={6}
              style={{ boxShadow: "2px 1px 2px black", borderRadius: "5px" }}
            >
              <Demo mySelect={onChange} myClick={onClick} />
            </Col>
            <Col span={4}></Col>
            <Col span={6}>
              <div
                style={{ boxShadow: "2px 1px 2px black", borderRadius: "5px" }}
              >
                <GeneralDisplayPage
                  GenKey={KeySelected.key}
                  isEdit={KeySelected.isEdit}
                  type={KeySelected.type}
                />
              </div>
            </Col>
            <Col spam={4}></Col>
          </Col>
        </Row>
      </Spin>
    </Layout>
  );
}

export default CoverPage;
