import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import "antd/dist/antd.css";
import { Form, Input, InputNumber, Button } from "antd";

import { getSpecificEmployeeByKey, getSpecificBranchByKey } from "./MockData";

const layout = {
  labelCol: {
    span: 8
  },
  wrapperCol: {
    span: 16
  }
};
const validateMessages = {
  required: "This field is required!",
  types: {
    email: "Not a validate email!",
    number: "Not a validate number!"
  },
  number: {
    range: "Must be between ${min} and ${max}"
  }
};
const defaultBranch = [
  {
    key: "0,B",
    data: [
      {
        name: "BranchCode",
        value: ""
      },
      {
        name: "BranchName",
        value: ""
      },
      {
        name: "BranchCategory",
        value: ""
      },
      {
        name: "ParentBranch",
        value: ""
      }
    ]
  }
];
const defaultEmployee = [
  {
    key: "0,E",
    data: [
      {
        name: "EmployeeNumber",
        value: "cc309921"
      },
      {
        name: "EmployeeName",
        value: "Mark"
      },
      {
        name: "EmployeeSurname",
        value: "Holloway"
      },
      {
        name: "EmployeeLastLoggedIn",
        value: "2020-03-12"
      },
      {
        name: "EmployeeID",
        value: "345864839579843"
      },
      {
        name: "EmployeeBranch",
        value: 57
      }
    ]
  }
];
export function GeneralDisplayPage(props) {
  const [Data, setData] = useState(defaultBranch);

  const onFinish = values => {
    console.log(values);
    //post it
  };
  useEffect(() => {
    if (props.GenKey !== undefined) {
      switch (props.type) {
        case "L":
        case "B":
          break;
        case "E":
          setData(
            props.isEdit ? getSpecificEmployeeByKey(splt[0]) : defaultEmployee
          );
          break;
        default:
          setData(
            props.isEdit ? getSpecificBranchByKey(splt[0]) : defaultBranch
          );
          break;
      }
    }
  }, [props.GenKey, props.isEdit, props.type]);
  return (
    <div>
      {Data.map(memes => {
        return (
          <>
            {/* {memes.isEdit ? `Editing for ${props.GenKey}` : `Adding new`} */}
            <h2>{props.isEdit ? "yes" : "no"}</h2>
            <Form
              {...layout}
              name="nest-messages"
              onFinish={onFinish}
              validateMessages={validateMessages}
            >
              {memes.data.map(field => {
                return (
                  <Form.Item label={field.name}>
                    <Input type="text" name={field.name} value={field.value} />
                  </Form.Item>
                );
              })}
              <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
                <Button type="primary" htmlType="submit">
                  Submit
                </Button>
              </Form.Item>
            </Form>
          </>
        );
      })}
    </div>
  );
}

GeneralDisplayPage.propTypes = {
  GenKey: PropTypes.string.isRequired,
  isEdit: PropTypes.bool.isRequired,
  type: PropTypes.string.isRequired
};

export default GeneralDisplayPage;
