import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import {getSpecificEmployeeByKey} from './MockData'


export function GeneralDisplayPage(props) {
    const [Data, setData] = useState([{Key: "0", Data: [{name: '', value: ''}]}]);
    
    useEffect(() => {
      const GetSpecificEmployee = async () => {
        setData(await getSpecificEmployeeByKey(props.GenKey[0]));
      };

      switch (props.GenKey[0]) {
        case 'Locale':
        case 'Branch':
        case 'Employee':
          GetSpecificEmployee();
          break;
          default:
           break;
      }
    }, [props.GenKey]);
    return (
      <ul
      >
        {Data[0].Key}
        {Data[0].Data.map(Values => (
          <li
            key={Values.name}
          >
            <p>{Values.name}</p>
            <input value={Values.value} />
          </li>
        ))}
      </ul>
    );

    
  
}


GeneralDisplayPage.propTypes = {
    GenKey: PropTypes.string.isRequired,
  };

  export default GeneralDisplayPage;
