import React, { useState } from 'react';
import { Tree } from 'antd';
import PropTypes from "prop-types";
import 'antd/dist/antd.css';
import { GetTreeData, GetTreeChildren} from './MockData'


function updateTreeData(list, key, children) {
  return list.map(node => {
    if (node.key === key) {
      return { ...node, children };
    }
    if (node.children) {
      return { ...node, children: updateTreeData(node.children, key, children) };
    }

    return node;
  });
}
function UpdateParent(ParentKey){
  //use this method to turn leaf on
  setTreeData(origin => treeData);
}
function GetChildNodes(ParentKey){
  let children = GetTreeChildren(ParentKey);
  if(children.length === 0)
    UpdateParent(ParentKey);
  return children;
}

 export function Demo(props)  {
  const [treeData, setTreeData] = useState(GetTreeData());

  function onLoadData({ key, children }) {
    return new Promise(resolve => {
      if (children) {
        resolve();
        return;
      }

        setTreeData(origin =>
          updateTreeData(origin, key, GetChildNodes(key)),
        );
        resolve();
    });
  }

  return <Tree loadData={onLoadData} onSelect={props.mySelect} treeData={treeData} />;
};

Demo.propTypes = {
  mySelect: PropTypes.func.isRequired,
};



export default Demo;
