import React, { useEffect, useState } from "react";
import { Tree, Menu, Dropdown, Spin, Layout, Input } from "antd";
import PropTypes from "prop-types";
import "antd/dist/antd.css";
//import { GetTreeData, GetTreeChildren } from "./MockData";
import { UserOutlined, HomeOutlined } from "@ant-design/icons";
import { TreeTree } from "./Tree";
import { TreeSearch } from "./TreeSearch";

//fixed calls
const rootURLCall = "https://localhost:5001/api/trees/"; //link to getting the root branches
const childURLCall = "https://localhost:5001/api/trees/"; //link to getting the children
const searchURLCall = "https://localhost:5001/api/tree/"; //link to search
const getEmployeeInfoCall = ""; //link to get all employee info
const getBranchInfoCall = ""; //link to get all branch info
const updateBranchCall = ""; //link to update the branch
const updateEmployeeCall = ""; //link to update the employee

export function BranchTree(props) {
  //hooks
  const [treeData, setTreeData] = useState([]);
  const [isLoading, setisLoading] = useState(false);
  const [search, setSearch] = useState("");

  function TreeUpdate(e) {
    if (!e) setTreeData([]);
    else setTreeData(e);
  }

  async function UpdateDrag(dragNode, node) {
    //the two nodes, need the info of dragNode, and the branchKey of node
    const newBranch = node.key;
    //see if dragnode is a branch or employee
    if (dragNode.isLeaf) {
      //is employee
      //get info of employee
      //update employee
      const url = `${props.searchURLCall}${e}`;
      let obj = [];
      const response = await fetch(url, {
        method: "GET",
      });
      const json = await response.json();
      json.map((Main) => {
        obj.push({
          title: Main.data[2].value,
          key: Main.data[1].value,
          icon:
            Main.data[3].value === "0" ? <HomeOutlined /> : <UserOutlined />,
          isLeaf: Main.data[3].value === "0" ? false : true,
        });
      });
      const empId = 0;
      const empIsActive = true;
      const empBranchId = newBranch;
      const empChannelId = 0;
      const empEmail = "";
      const empFirstName = "";
      const empLastName = "";
      const empBankerRoleId = 0;
      const empStaffNo = "";
    } else {
      //is branch
      //get info of branch
      //update branch
      const Id = 0;
      const BranchCode = 0;
      const BranchName = "";
      const BranchCategoryId = 0;
      const ChannelId = 0;
      const CanReceiveSale = true;
      const ParentBranchId = 0; //or null
      const RegionId = 0; //or null
      const IsActive = true;
      const IsPostBox = true;
      const OldCode = 0; //or null
    }
  }

  //reset tree data each time rootBranch gets updated
  useEffect(() => {}, [props.rootBranch]);

  return (
    <Layout>
      <Spin spinning={isLoading}>
        <TreeSearch searchURLCall={searchURLCall} UpdateTree={TreeUpdate} />

        <TreeTree
          data={treeData}
          rootBranch="0"
          UpdateDrag={UpdateDrag}
          onRightClick={props.onRightClick}
          onSelect={props.onSelect}
          rootURLCall={rootURLCall}
          childURLCall={childURLCall}
        />
      </Spin>
    </Layout>
  );
}
BranchTree.propTypes = {
  rootBranch: PropTypes.string.isRequired,
  onSelect: PropTypes.func.isRequired,
  onRightClick: PropTypes.func.isRequired,
  isBranch: PropTypes.bool, //leave for now
  form: PropTypes.bool, //leave for now
};
