import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import 'antd/dist/antd.css';
import { Form, Input, InputNumber, Button } from 'antd';

import {getSpecificEmployeeByKey, getSpecificBranchByKey} from './MockData'

const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
  const validateMessages = {
    required: 'This field is required!',
    types: {
      email: 'Not a validate email!',
      number: 'Not a validate number!',
    },
    number: {
      range: 'Must be between ${min} and ${max}',
    },
  };
  const defaultBranch = [{key: "0,B", data: [{name: '', value: ''}]}];
  const defaultEmployee = [{key: "0,E", data: [{name: '', value: ''}]}];
export function GeneralDisplayPage(props) {
    const [Data, setData] = useState(defaultBranch);
    
  const onFinish = values => {
    console.log(values);
    //post it
  };
    useEffect(() => {
        if(props.GenKey !== undefined)
        {
      let splt = props.GenKey.split(',');
      switch (splt[splt.length - 1]) {
        case 'L':
        case 'B':
            break;
        case 'E':
            setData(props.isEdit ? getSpecificEmployeeByKey(splt[0]) : defaultEmployee);
          break;
          default:
            setData(props.isEdit ? getSpecificBranchByKey(splt[0]) : defaultBranch);
           break;
      }
      
    }}, [props.GenKey]);
    return (
        <div>
            {Data.map(memes => {
                return(
                    <>
                    {/* {memes.isEdit ? `Editing for ${props.GenKey}` : `Adding new`} */}
                <h2>{props.isEdit}</h2>
    <Form {...layout} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
        {memes.data.map(field => {
            return(
                <Form.Item 
                name={field.name}
                label={field.name}
                >
                <Input type="text" value={field.value}/>    
                </Form.Item>
            );
        })}
      <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>
    </>
            );})}
            </div>

        // <div>
        // {Data.map(memes => {
        //     return(<form>
        //         <h2>{memes.key}</h2>
        //         {memes.data.map(Values => {
        //             return(
        //                 <li key={`${memes.key}${Values.name}`}>
        //                     <p><b>{Values.name}</b></p>
        //                     <p>{Values.value}</p>
        //                 </li>
        //             );
        //         })}
        //         </form>
        //     );
        // })}
        // </div>
    );
}


GeneralDisplayPage.propTypes = {
    GenKey: PropTypes.string.isRequired,
  };

  export default GeneralDisplayPage;
