import React, { useState, useEffect } from "react";
import {Demo} from "./AntTree";
import {GeneralDisplayPage} from "./GeneralManage";
import { Link } from "react-router-dom";
import { loadCourses, deleteCourse } from "../actions/courseActions";

function CoverPage() {
  const [KeySelected, setKeySelected] = useState({
    key: '1',
    isEdit: true,
    type: 'Branch'
  });

  useEffect(() => {

  }, [KeySelected]);


  const onChange = (selectedKeys) => {
      if(selectedKeys[0] !== undefined)
    setKeySelected({key: selectedKeys[0], isEdit: true,
        type: selectedKeys[0].split(',')[1] === 'B' ? 'Branch': 'Employee'});
  };
  const onClickBranch = (value) => {
      alert(value);
      setKeySelected({key: '', isEdit: false,
      type: value});
  };
  const onClickEmployee = (value) => {
      setKeySelected({key: '', isEdit: false,
      type: 'Branch'});
  };

  return (
    <>
      <h2>Hunting 2.0!</h2>
      add Tree here
      <div className="row">
      <div className="col-md-6"><Demo mySelect={onChange} EmpClick={onClickEmployee} BraClick={onClickBranch}/></div>
      <div className="col-md-6"><GeneralDisplayPage GenKey={KeySelected.key} isEdit={KeySelected.isEdit} type={KeySelected.type}/></div>
      </div>
    </>
  );
}

export default CoverPage;
